import { Component, OnInit } from "@angular/core"
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from "@angular/forms"
import { IQuestion } from "./question"
import { Router } from "@angular/router"
import { HttpClient } from "@angular/common/http"
import { QuestionService } from "./questions.service"
import { AuthService } from "../authentication/authentication.service"

@Component({
  selector: "app-questionupdate-component",
  templateUrl: "./questionupdate.component.html",
})

export class QuestionupdateComponent {
  questionUpdateForm: FormGroup;

  constructor(private _auth: AuthService, private _formbuilder: FormBuilder, private _router: Router, private _questionService: QuestionService) {
    this.questionCreateForm = _formbuilder.group({
      title: ["", Validators.required],
      content: ["", Validators.required],
    });
  }

  onSubmit() {
    console.log(this.questionUpdateForm)

    const newQuestion = this.questionUpdateForm.value

    this._questionService.createQuestion(newQuestion)
      .subscribe(response => {
        if (response.success) {
          console.log(response.message)
          this._router.navigate(["/questions"])
        }
        else {
          console.log("Question update failed")
        }
      })
  }
}
<h1 class="text-center mb-3">Update question</h1>

<form [formGroup]="questionUpdateForm" (ngSubmit)="onSubmit()" class="d-flex flex-column gap-4">

    <app-questionForm-component [parentForm]="questionUpdateForm" class="d-flex flex-column gap-4"></app-questionForm-component>

    <div class="d-flex gap-3">
        <button type="submit" [disabled]="questionUpdateForm.invalid" class="btn btn-primary">Post</button>
        <a class="btn btn-secondary" [routerLink]="['/userquestions']">Cancel</a>
    </div>
</form>
