import { Component, Input } from "@angular/core"
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from "@angular/forms"
import { IReply } from "../replies/reply"

@Component({
  selector: "app-question-reply-card-component",
  templateUrl: "./questionReplyCard.component.html",
})

export class QuestionReplyCardComponent {
  @Input() reply!: IReply;

  constructor(private _formbuilder: FormBuilder) {

    // Initializing the reply form with form controls and validation
    this.replyUpdateForm = _formbuilder.group({
      content: [this.reply.content || "", Validators.required],
    });
  }

  replyUpdateForm: FormGroup;

  // Toggles the 'edit' property for the clicked reply
  toggleEdit(reply: IReply) {
    reply.edit = !reply.edit; 
  }

  onSubmit() {

  }
}
<div class="d-flex gap-4 w-100">
    <div class="user-img-container">
        <img class="img-fluid rounded-circle" [src]="reply.user.imageUrl" alt="User image" />
    </div>
    <div class="w-100">
        <div>
            <h6 class="fw-bolder m-0">{{ reply.user.userName }}</h6>
            <p class="mb-2">{{ reply.created | date: 'dd-MM-yyyy HH:mm'  }}</p>
        </div>
        <p *ngIf="!reply.edit" class="m-0">{{ reply.content }}</p>

         <form [formGroup]="replyUpdateForm"  (ngSubmit)="onSubmit()">
            <div class="form-group mb-3">
                <textarea rows="3" formControlName="content" class="form-control py-1 px-2">{{ reply.content }}</textarea>
            </div>
            <div class="d-flex gap-3">
                <button type="submit" [disabled]="replyUpdateForm.invalid" class="btn btn-primary">Update</button>
                <a class="btn btn-secondary" (click)="toggleEdit(reply)">Cancel</a>
            </div>
        </form>
    </div>
</div>

<style>
    .user-img-container {
        width: 50px;
    }
</style>
