import { Component, OnInit } from "@angular/core"
import { AuthService } from "./authentication.service"
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from "@angular/forms"

@Component({
  selector: "app-login-component",
  templateUrl: "./login.component.html",
})

export class LoginComponent {
  loginForm: FormGroup;
  loginError:string = ""

  constructor(private _formbuilder: FormBuilder, private _authService: AuthService) {
    this.loginForm = _formbuilder.group({
      username: ["", Validators.required],
      password: ["", Validators.required]
    });
  }

  get loggedIn() {
    return this._authService.isLoggedIn;
  }

  loginUser() {
    const user = this.loginForm.value

    this.authService.login(user)
      .subscribe(response => {
        if (response.success) {
          this.authService.setLoggedIn(true);
          this.authService.setLoggedInUser(response.username);
          console.log("User logged in:", response.username);
          console.log(response.message);
          // Clear any previous login error message
          this.loginError = '';
        } else {
          console.log("Login failed");
          // Set error message to display in the UI
          this.loginError = "Login failed. Please check your credentials.";
        }
      }, error => {
        console.error("Error occurred:", error);
        // Set error message for network or server errors
        this.loginError = "An error occurred. Please try again later.";
      });
  }

  }
}
