import { Component, Input, Output, EventEmitter } from "@angular/core";
import { IQuestion } from "../questions/question"
import { Router } from "@angular/router"
import { QuestionService } from "../questions/questions.service"

@Component({
  selector: "app-deleteModal-component",
  templateUrl: "./deleteModal.component.html",
})

export class DeleteModalComponent  {
  @Input() question!: IQuestion;
  @Output() deletionComplete: EventEmitter<number> = new EventEmitter<number>();
  modalVisible: boolean = false;

  constructor(private _router: Router, private _questionService: QuestionService) { }

  openModal(): void {
    this.modalVisible = true; // Show the modal
  }

  closeModal(): void {
    this.modalVisible = false; // Hide the modal
  }

  deleteQuestion(question: IQuestion): void {
      this._questionService.deleteQuestion(question.questionId)
        .subscribe(
          (response) => {
            if (response.success) {



              this.deletionComplete.emit(question.questionId);
              this.closeModal();

              console.log(response.message);
            }
          },
          (error) => {
            console.error('Error deleting question:', error);
          });

  }


}

<div class="modal fade" [class.show]="modalVisible" #modal [id]="'modal-' + question.questionId" tabindex="-1" [attr.aria-labelledby]="'modal-' + question.questionId" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h4 class="modal-title" id="exampleModalLabel">Delete confirmation</h4>
                <button type="button" class="py-0 px-2 exit-btn" data-bs-dismiss="modal" aria-label="Close">
                    <i class="fa-solid fa-xmark fa-lg"></i>
                </button>
            </div>
            <hr class="my-0 mx-3" />
            <div class="modal-body">
                <h6>Are you sure you want to delete the following post?</h6>
                <p class="fw-bolder my-3 mx-0">"{{ question.title }}"</p>
            </div>
            <div class="modal-footer">
                <a class="btn btn-secondary" data-bs-dismiss="modal">Close</a>
                <button type="button" class="btn btn-danger" (click)="deleteQuestion(question)">Delete</button>
            </div>
        </div>
    </div>
</div>


<style>
    .modal-content {
        background: #2c374e;
        border-radius: 0;
    }

    .modal-footer, .modal-header {
        border: none;
    }

    .exit-btn {
        border: none;
    }

    button {
        outline: none;
    }
</style>
