import { Component } from '@angular/core';
import { AuthService } from "../authentication/authentication.service"

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})

export class NavMenuComponent {

  constructor(private _authService: AuthService) {

  }

  isExpanded = false;

  collapse() {
    this.isExpanded = false;
  }

  toggle() {
    this.isExpanded = !this.isExpanded;
  }

  get loggedIn() {
    return this._authService.isLoggedIn;
  }
}

<header>
    <nav class="navbar navbar-expand-lg navbar-dark py-2 px-3 ">
        <div class="container-fluid p-0">
            <a class="navbar-brand m-0 align-items-center d-flex gap-2" [routerLink]="['/']">
                <img class="logo" src="../assets/img/logo.png" alt="Logo image" />
                <span class="logoText"><strong>CodeHub</strong></span>
            </a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarText" aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarText">
                <ul class="navbar-nav mx-auto gap-4">
                    <li class="nav-item text-uppercase">
                        <a class="hover-underline" [routerLink]="['/questions']">Browse questions</a>
                    </li>
                    <li class="nav-item text-uppercase">
                        <a class="hover-underline">My questions</a>
                    </li>
                    <li class="nav-item text-uppercase">
                        <a class="hover-underline" [routerLink]="['/questionform']">Post a question</a>
                    </li>
                </ul>

                <div class="loginBtn-container">
                    <ul class="navbar-nav d-flex gap-3">
                        <li class="nav-item text-uppercase d-flex align-items-center">
                            <a class="hover-underline" id="register" [routerLink]="['/auth/register']">Register</a>
                        </li>
                        <li class="nav-item">
                            <a id="login" [routerLink]="['/auth/login']">
                                <button class="text-uppercase loginBtn">Log in</button>
                            </a>
                        </li>
                    </ul>
                </div>
            </div>
        </div>
    </nav>
</header>

<style>
    label {
        color: #6f7174;
    }

    .logoutBtn:hover {
        color: #649ef4;
    }
</style>
